mapper.select:
select * from cost_t where

    AND Cost_T.costDatetime >= #{beginDate}


    AND Cost_T.costDatetime <= #{endDate}

select * from Cost_T where userNo = #{userNo}

    AND Cost_T.costDatetime >= #{beginDate}


    AND Cost_T.costDatetime <= #{endDate}

mapper.update:
update Cost_T
set costEvent  = #{costEvent},
    costAmount = #{costAmount},
    costRemark = #{costRemark}
where costNo = #{costNo}
