mapper.select:
select * from Income_T where

    AND Income_T.incDatetime >= #{beginDate}


    AND Income_T.incDatetime <= #{endDate}

select * from Income_T where userNo = #{userNo}

    AND Income_T.incDatetime >= #{beginDate}


    AND Income_T.incDatetime <= #{endDate}
AND userNo = #{userNo}

mapper.update:
update Income_T
set incEvent  = #{incEvent},
    incAmount = #{incAmount},
    incRemark = #{incRemark}
where incNo = #{incNo}
